#!/usr/bin/env python3
import os
from fontTools.ttLib import TTFont

def convert_otf_to_ttf(otf_path, ttf_path):
    """Convert OTF font to TTF format"""
    try:
        font = TTFont(otf_path)
        font.save(ttf_path)
        print(f"Converted: {os.path.basename(otf_path)} -> {os.path.basename(ttf_path)}")
        return True
    except Exception as e:
        print(f"Error converting {otf_path}: {e}")
        return False

def main():
    fonts_dir = "fonts"

    if not os.path.exists(fonts_dir):
        print(f"Directory {fonts_dir} not found!")
        return

    otf_files = [f for f in os.listdir(fonts_dir) if f.endswith('.otf')]

    if not otf_files:
        print("No OTF files found in fonts directory!")
        return

    print(f"Found {len(otf_files)} OTF files to convert:")

    converted_count = 0
    for otf_file in otf_files:
        otf_path = os.path.join(fonts_dir, otf_file)
        ttf_file = otf_file.replace('.otf', '.ttf')
        ttf_path = os.path.join(fonts_dir, ttf_file)

        if convert_otf_to_ttf(otf_path, ttf_path):
            converted_count += 1

    print(f"\nConversion complete! {converted_count}/{len(otf_files)} files converted successfully.")

if __name__ == "__main__":
    main()
